import React from 'react';
import ReactDOM from 'react-dom';
import Clock from './clock';
import TimeInfo from './timeInfo';

class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            times: []
        };
    }
    onHandleSubmit(data) {
        this.setState(preState => ({
            times: [...preState.times, { ...data,id: Date.now() }]
        }));
        console.log(data);
    }
    deleteTime(id){
        this.setState(prevState=>({
            times: prevState.times.filter(time=> time.id !== id)
        }))
    }
    buildTimes() {
        if (this.state.times < 1) {
            return null;
        }
        return this.state.times.map((time) => {
            const { value, id } = time;
            return <TimeInfo 
                    key={id} 
                    value={value} 
                    handleDelete={()=>this.deleteTime(id)} 
                    />
        })
    }
    render() {
        return (
            <div className="container">
                <h1>World Clocks</h1>
                <Clock  onFormSubmit={(data)=> this.onHandleSubmit(data)}/>
                <hr />
                {this.buildTimes()}
            </div>
        )
    }
}

ReactDOM.render(<App />, document.getElementById('root'))
