import React from 'react';
import moment from 'moment-timezone';
import './style.css'

class ClockForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            form: {
                timeZones: moment.tz.names(),
                value: "",
                valid:false
            }
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event,value,valid) {
        const {form }=this.state;
        const newField = {value, valid:true, error:""};
        switch(field){
            case'form':{
                if(!value){
                    newField.error= 'Please select one';
                    newField.valid=false;
                }
                break
            }
        }
        this.setState({ 
            form:{
                ...form,
                [field]:newField,
                value: event.target.value
            }
         });

    }
    handleSubmit(event) {
        event.preventDefault();
        this.props.onFormSubmit(this.state);


        this.setState({
            timeZones: moment.tz.names(),
            value: ""
        })
    }
    handleOption() {
        return (this.state.timeZones.map((option, index) => {
            return (
                <option value={"Local time in " + option + " is " + moment().tz(option).format("h:mm:ss A")} key={index}>{option}</option>
            )
        })
        )
    }
    render() {
        return (
            <div className="wrapper">
                <h1>World Clocks</h1>
                <form onSubmit={this.handleSubmit}>
                    <button className="btn btn-primary">Add Clock</button>
                    <select onChange={this.handleChange}>

                        {this.handleOption()}
                    </select>
                </form>
            </div>
        )
    }

}

export default ClockForm
