    constructor(props) {
        super(props);
        this.state = {
            timeZones: moment.tz.names()
        }
    }
    handleClick(){
        var value = React.findDOMNode(this.refs.mySelect).value;
    }
    handleOption(){
        return (this.state.timeZones.map((option, index) => {
            return (
                <option value={moment().tz(option).format("h:mm:ss A")} key={index}>{option}</option>
            )
        })
        )}
    handleValue(){

    }


    render() {

        return (
            <>
            <LocalTime />
                <div className="wrapper">
                    <h1>World Clocks</h1>
                    <form>
                        <select ref="mySelect">
                            {this.handleOption()}
                        </select>   
                        <button onClick={this.handleClick} className="btn btn-primary">Add Clock</button>
                    </form>
                    <div className="body">
                    <p></p>

                    </div>


                </div>
            </>
        )
    }

}

export default Clock
