import React from 'react';
import moment from 'moment-timezone';
import './style.css'

// console.log(moment.tz.names());

 console.log(moment().tz("china").format("h:mm:ss A"))




class Clock extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            timeZones: moment.tz.names()
        }
    }


    render() {
        var options = this.state.timeZones.map((option, index) => {
            return (
                <option key={index}>{option}</option>
            )
        })
        return (
            <>

                <div className="wrapper">
                    <h1>World Clocks</h1>
                    <form>
                        <select >
                            {options}
                        </select>   
                        <button className="btn btn-primary">Add Clock</button>
                    </form>
                    <div className="body">

                    </div>


                </div>
            </>
        )
    }

}

export default Clock
