import React from 'react';
import moment from 'moment-timezone';
import './style.css'
import LocalTime from './localTime';
import TimeInfo from './timeInfo';


// console.log(moment.tz.names());

console.log(moment().tz("Asia/Taipei").format("h:mm:ss A"))



class Clock extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            timeZones: moment.tz.names(),
            value: ""
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({ value: event.target.value });
    }
    handleSubmit(event) {
        alert(this.state.value);
        event.preventDefault();
    }
    handleOption() {
        return (this.state.timeZones.map((option, index) => {
            return (
                <option value={"Local time in "+option+" is "+moment().tz(option).format("h:mm:ss A")} key={index}>{option}</option>
            )
        })
        )
    }
    render() {
        var selectedTime = this.state.value
        return (
            <>
                <LocalTime />
                <div className="wrapper">
                    <form onSubmit={this.handleSubmit}>
                        <select onChange={this.handleChange}>
                            {this.handleOption()}
                        </select>
                        <button className="btn btn-primary">Add Clock</button>
                    </form>
                    <div className="body">
                        <p>{selectedTime}</p>

                    </div>


                </div>
            </>
        )
    }

}

export default Clock
